import { Component, DestroyRef, inject, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormGroup,
  FormsModule,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { TranslateModule } from '@ngx-translate/core';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';

import { NzTabsModule } from 'ng-zorro-antd/tabs';
import { NzFormModule } from 'ng-zorro-antd/form';
import { NzInputModule } from 'ng-zorro-antd/input';
import { NzImageModule } from 'ng-zorro-antd/image';
import { NzButtonModule } from 'ng-zorro-antd/button';
import { NZ_MODAL_DATA, NzModalRef } from 'ng-zorro-antd/modal';

import { NewsFirebaseService } from '@shared/services';
import { EditorComponent } from './components';
import { INews } from '@shared/interfaces';

@Component({
  selector: 'app-add-news-modal',
  standalone: true,
  imports: [
    TranslateModule,
    FormsModule,
    ReactiveFormsModule,
    EditorComponent,
    NzFormModule,
    NzInputModule,
    NzButtonModule,
    NzTabsModule,
    NzImageModule,
  ],
  templateUrl: './add-news-modal.component.html',
  styleUrl: './add-news-modal.component.scss',
})
export class AddNewsModalComponent implements OnInit {
  private _destroyRef = inject(DestroyRef);
  private _newsFirebase = inject(NewsFirebaseService);

  readonly #modal = inject(NzModalRef);
  readonly news?: INews = inject(NZ_MODAL_DATA);
  private _fb = inject(FormBuilder);

  public formUA!: FormGroup;
  public formEN!: FormGroup;
  public isLoading = false;
  public imgURL = '';
  public fallback =
    'data:image/png;base64,';

  ngOnInit(): void {
    this.imgURL = this.news?.img_url || '';

    this.formUA = this._fb.group({
      title: [this.news?.ua.title || '', [Validators.required]],
    });
    this.formEN = this._fb.group({
      title: [this.news?.en?.title || '', [Validators.required]],
    });
  }

  public addFormControlUA(name: string, control: FormGroup) {
    this.formUA.addControl(name, control);
  }

  public addFormControlEN(name: string, control: FormGroup) {
    this.formEN.addControl(name, control);
  }

  public submit(status: 'draft' | 'published') {
    if (this._isFormValid()) {
      this.isLoading = true;

      const body = this._createRequestBody(status);

      if (this.news?.id) {
        this._update(body);
      } else {
        this._addNews(body);
      }
    } else {
      this._markInvalidControls(this.formUA);
      this._markInvalidControls(this.formEN);
    }
  }

  private _isFormValid(): boolean {
    return this.formUA.valid && this.formEN.valid && !!this.imgURL;
  }

  private _createRequestBody(status: 'draft' | 'published'): Omit<INews, 'id'> {
    const ua = this.formUA.value;
    const en = this.formEN.value;

    return {
      ua: {
        title: ua.title,
        description: ua.description.text,
        content: ua.content.text,
      },
      en: {
        title: en.title,
        description: en.description.text,
        content: en.content.text,
      },
      img_url: this.imgURL,
      status,
      ref:
        this.news?.ref ||
        this.formEN.value.title.trim().replace(/[^\w\d]/g, '-'),
      created_at: this.news?.created_at || new Date().toISOString(),
      updated_at: new Date().toISOString(),
    };
  }

  private _markInvalidControls(form: FormGroup): void {
    Object.values(form.controls).forEach((control) => {
      if (control.invalid) {
        control.markAsDirty();
        control.updateValueAndValidity({ onlySelf: true });
      }
    });
  }

  private _addNews(body: Omit<INews, 'id'>) {
    this._newsFirebase
      .addNews(body)
      .pipe(takeUntilDestroyed(this._destroyRef))
      .subscribe({
        next: () => {
          this.isLoading = false;
          this.destroy({ ...body, id: '' });
        },
      });
  }

  private _update(body: Omit<INews, 'id'>) {
    this._newsFirebase
      .update(this.news!.id, body)
      .pipe(takeUntilDestroyed(this._destroyRef))
      .subscribe({
        next: () => {
          this.isLoading = false;
          this.destroy({ ...body, id: this.news!.id });
        },
      });
  }

  public destroy(data: INews | null = null): void {
    this.#modal.destroy(data);
  }
}
